mapper.update:
UPDATE 
SET utime = unix_timestamp(now())

      ,start_time = #{startTime}


      ,end_time = #{endTime}


      ,valid = #{valid}

  WHERE store_no = #{storeNo} AND discount_id = #{discountId}

mapper.select:
SELECT discount_id FROM 
WHERE valid = 1

    AND store_no = #{storeNo}


    AND parent_store_no = #{parentStoreNo}


    AND end_time <= unix_timestamp(now())


    AND start_time <= unix_timestamp(now()) and end_time >= unix_timestamp(now())


    AND ( start_time >= unix_timestamp(now()) OR status = 2)

ORDER BY id DESC
limit #{startNo},#{pageSize}

SELECT count(1) FROM WHERE valid = 1
AND store_no = #{storeNo}
AND parent_store_no = #{parentStoreNo}
AND end_time <= unix_timestamp(now())
AND start_time <= unix_timestamp(now()) and end_time >= unix_timestamp(now())
AND ( start_time >= unix_timestamp(now()) OR status = 2)
